import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import { DARK_PINK_COLOR } from "../color/Theme";

const HeaderArea = styled.div`
  width: 100%;
  height: 10vh;

  position: absolute;
  display: flex;
  justify-content: space-evenly;
  z-index: 99999;

  background-color: #f0f0f0;
  overflow: hidden;
  margin: 0;
  padding: 0;
`;

{
  /* z-index -> 화면이 그려질 때 어떤 요소가 위에 올라가는지 설정하는 속성
    값이 높으면 높을수록 위에 올라가며 z축 기준으로 순서를 가짐 */
}

const StyledLink = styled(Link)`
  text-decoration: none;
  color: #000;
  font-weight: bold;
  padding: 10px;
  transition: color 0.3s ease;

  &:hover {
    color: #000;
  }
`;

const Header = () => {
  return (
    <HeaderArea>
      <StyledLink to="/main">Main</StyledLink>
      <StyledLink to="/sub-main1">Sub xMain 1</StyledLink>
      <StyledLink to="/sub-main2">Sub Main 2</StyledLink>
    </HeaderArea>
  );
};

export default Header;
